function r(s){return new Promise(e=>{setTimeout(()=>{switch(s.action){case"login":s.username==="admin"&&s.password==="password"?e({success:!0,user:{username:"admin",fullName:"Admin User",role:"Admin"}}):s.username==="messenger"&&s.password==="password"?e({success:!0,user:{username:"messenger",fullName:"Messenger User",role:"Messenger"}}):e({success:!1,error:"ชื่อผู้ใช้หรือรหัสผ่านไม่ถูกต้อง"});break;case"register":e({success:!0,message:"ลงทะเบียนสำเร็จ"});break;case"createJob":e({success:!0,message:"บันทึกงานสำเร็จ",jobId:"JOB-"+Date.now()});break;case"getJobs":e({success:!0,jobs:[{jobId:"JOB-001",timestamp:new Date().toISOString(),status:"complete",jobDate:new Date().toISOString(),company:"ทดสอบ บริษัท",assignedBy:"ทดสอบ ผู้สั่งงาน",contact:"0123456789",pickupProvince:"กรุงเทพมหานคร",pickupDistrict:"บางนา",totalAmount:1e3}]});break;default:e({success:!0,message:"ดำเนินการสำเร็จ"})}},1e3)})}async function n(s){if(window.location.hostname==="localhost"||window.location.hostname==="127.0.0.1")return r(s);if(!window.GOOGLE_SCRIPT_URL)throw new Error("Google Script URL ไม่ได้ถูกกำหนดไว้");try{const e=await fetch(window.GOOGLE_SCRIPT_URL,{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(s)});if(!e.ok)throw new Error(`HTTP error! status: ${e.status}`);return await e.json()}catch(e){throw new Error(`ไม่สามารถเชื่อมต่อกับ Google Apps Script ได้: ${e.message}`)}}export{n as submitToGoogleSheetsInternal};
